import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./Home.css"; // Import your CSS file for styling

const Home = () => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    // After component mounts, set isVisible to true to trigger animation
    setIsVisible(true);
  }, []);

  return (
    <div className="Home">
      <div className="Left">
        <h1>
          {/* Wrap each letter in a span with animation class */}
          {isVisible &&
            "BEAUTYPLEX".split("").map((char, index) => (
              <span key={index} className="animation">
                {char}
              </span>
            ))}
        </h1>
        <h2>
          Bienvenue dans notre boutique de produits cosmétiques, où chaque
          produit est soigneusement sélectionné pour vous offrir une expérience
          de beauté incomparable
        </h2>
        <h3>Découvrez une sélection soigneusement choisie pour sublimer votre beauté naturelle.</h3>
        <Link to={"/products"} id="hh">
          <a id="ExploreProduct">Explorer les produits</a>
        </Link>
      </div>
      <div className="Right">
        <img id="product" src="./bioplex.png" alt="bioplex" />
      </div>
    </div>
  );
};

export default Home;
